// Dynamically load CSS
(function () {
    const link = document.createElement('link');
    link.rel = 'stylesheet';
    link.href = 'https://praweensoni.github.io/notification-Style/style.css';
    document.head.appendChild(link);
  })();
  
  // Toast notification logic
  function showToast(type) {
    const toastDetails = {
      success: {
        icon: 'fa-circle-check',
        text: 'Success: This is a success message.',
      },
      error: {
        icon: 'fa-xmark',
        text: 'Error: This is an error message.',
      },
      warning: {
        icon: 'fa-triangle-exclamation',
        text: 'Warning: This is a warning message.',
      },
      info: {
        icon: 'fa-circle-info',
        text: 'Info: This is an info message.',
      },
    };
  
    const notification = document.querySelector('.notification') || createNotificationContainer();
    const { icon, text } = toastDetails[type];
  
    const toast = document.createElement('div');
    toast.className = `toast ${type}`;
    toast.innerHTML = `
      <div class="toast-icon"><i class="fa ${icon}"></i></div>
      <div class="toast-message">${text}</div>
      <div class="toast-close" onclick="this.parentElement.remove()">&times;</div>
    `;
  
    notification.appendChild(toast);
    setTimeout(() => toast.remove(), 5000);
  }
  
  function createNotificationContainer() {
    const container = document.createElement('div');
    container.className = 'notification';
    document.body.appendChild(container);
    return container;
  }
  