// Dynamically load CSS
(function () {
    const link = document.createElement("link");
    link.rel = "stylesheet";
    link.href = "toastnotifypks.css";
    link.href = 'https://praweensoni.github.io/notification-Style/toastnotifypks.css';
  })();
  
  // Toast notification logic
  function showToast(type, ctmMsg, Time) {
    const toastDetails = {
      success: {
        icon: '<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="#0abf30" viewBox="0 0 512 512"><path d="M256 512A256 256 0 1 0 256 0a256 256 0 1 0 0 512zM369 209L241 337c-9.4 9.4-24.6 9.4-33.9 0l-64-64c-9.4-9.4-9.4-24.6 0-33.9s24.6-9.4 33.9 0l47 47L335 175c9.4-9.4 24.6-9.4 33.9 0s9.4 24.6 0 33.9z"/></svg>',
        notifiHeadMsg: "Success",
        notifiMsg: "This is success Message.",
      },
      error: {
        icon: '<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="#e24d4c" viewBox="0 0 512 512"><path d="M256 512A256 256 0 1 0 256 0a256 256 0 1 0 0 512zM175 175c9.4-9.4 24.6-9.4 33.9 0l47 47 47-47c9.4-9.4 24.6-9.4 33.9 0s9.4 24.6 0 33.9l-47 47 47 47c9.4 9.4 9.4 24.6 0 33.9s-24.6 9.4-33.9 0l-47-47-47 47c-9.4 9.4-24.6 9.4-33.9 0s-9.4-24.6 0-33.9l47-47-47-47c-9.4-9.4-9.4-24.6 0-33.9z"/></svg>',
        notifiHeadMsg: "Error",
        notifiMsg: "This is an error message.",
      },
      warning: {
        icon: '<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="#e9bd0c" viewBox="0 0 512 512"><path d="M256 32c14.2 0 27.3 7.5 34.5 19.8l216 368c7.3 12.4 7.3 27.7 .2 40.1S486.3 480 472 480L40 480c-14.3 0-27.6-7.7-34.7-20.1s-7-27.8 .2-40.1l216-368C228.7 39.5 241.8 32 256 32zm0 128c-13.3 0-24 10.7-24 24l0 112c0 13.3 10.7 24 24 24s24-10.7 24-24l0-112c0-13.3-10.7-24-24-24zm32 224a32 32 0 1 0 -64 0 32 32 0 1 0 64 0z"/></svg>',
        notifiHeadMsg: "Warning",
        notifiMsg: "This is a warning message.",
      },
      info: {
        icon: '<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="#3498db" viewBox="0 0 512 512"><path d="M256 512A256 256 0 1 0 256 0a256 256 0 1 0 0 512zM216 336l24 0 0-64-24 0c-13.3 0-24-10.7-24-24s10.7-24 24-24l48 0c13.3 0 24 10.7 24 24l0 88 8 0c13.3 0 24 10.7 24 24s-10.7 24-24 24l-80 0c-13.3 0-24-10.7-24-24s10.7-24 24-24zm40-208a32 32 0 1 1 0 64 32 32 0 1 1 0-64z"/></svg>',
        notifiHeadMsg: "Info",
        notifiMsg: "This is an info message.",
      },
    };
  
    const notification =
      document.querySelector(".notification") || createNotificationContainer();
  
    const { icon, notifiHeadMsg, notifiMsg } = toastDetails[type];
  
    const finalHeadText = type || notifiHeadMsg;
    const finalText = ctmMsg || notifiMsg;
  
    // Create the toast element
    const toast = document.createElement("div");
    toast.className = `toast ${type}`;
    toast.innerHTML = `
        <div class="toast-icon">${icon}</div>
        <div class="toast-message"><p>${finalHeadText} :</p>${finalText}</div>
        <div class="toast-close" onclick="this.parentElement.remove()">&times;</div>
      `;
  
    // Add the line animation using ::before dynamically
    const duration = Time || 5000; // Default duration is 5 seconds
    toast.style.setProperty("--duration", `${duration}ms`);
  
    // Append to notification container
    notification.appendChild(toast);
  
    // Remove the toast after the duration
    setTimeout(() => toast.remove(), duration);
  }
  
  function createNotificationContainer() {
    const container = document.createElement("div");
    container.className = "notification";
    document.body.appendChild(container);
    return container;
  }
  