(() => {
    const toastDetails = {
      timer: 5000,
      success: {
        icon: 'fa-circle-check',
        text: 'Success: This is a success notification.',
      },
      error: {
        icon: 'fa-xmark',
        text: 'Error: This is an error notification.',
      },
      warning: {
        icon: 'fa-triangle-exclamation',
        text: 'Warning: This is a warning notification.',
      },
      info: {
        icon: 'fa-circle-info',
        text: 'Info: This is an info notification.',
      },
    };
  
    const createToastContainer = () => {
      let container = document.querySelector(".notification");
      if (!container) {
        container = document.createElement("ul");
        container.className = "notification";
        document.body.appendChild(container);
      }
      return container;
    };
  
    const removeToast = (toast) => {
      toast.classList.add("hide");
      if (toast.timeoutId) clearTimeout(toast.timeoutId);
      setTimeout(() => toast.remove(), 500);
    };
  
    const showToast = (type, customMessage, customTimer) => {
      const container = createToastContainer();
      const { icon, text } = toastDetails[type] || {};
      const message = customMessage || text || "Notification!";
      const timer = customTimer || toastDetails.timer;
  
      const toast = document.createElement("li");
      toast.className = `toast ${type}`;
      toast.innerHTML = `
        <div class="column">
          <i class="fa-solid ${icon}"></i>
          <span>${message}</span>
        </div>
        <i class="fa-solid fa-xmark" onclick="(${removeToast.toString()})(this.parentElement)"></i>
      `;
      container.appendChild(toast);
      toast.timeoutId = setTimeout(() => removeToast(toast), timer);
    };
  
    // Expose the `showToast` function globally
    window.showToast = showToast;
  })();
  